var autocomplete;function initAutocomplete(){(autocomplete=new google.maps.places.Autocomplete(document.getElementById("location-input"),{types:["geocode"]})).addListener("place_changed",fillInAddress)}function fillInAddress(){var e=autocomplete.getPlace();document.getElementById("lat").value=e.geometry.location.lat(),document.getElementById("lng").value=e.geometry.location.lng(),document.getElementById("location-selected-text").value=e.formatted_address}